import './App.css';
import {
    LineChart,
    BarChart,
    ResponsiveContainer,
    Legend, Tooltip,
    Line,
    XAxis,
    YAxis,
    CartesianGrid
} from 'recharts';
import { Component } from 'react';
import useWebSocket from 'react-use-websocket';


class OIChart extends Component {
    constructor(props) {
        super(props);
        this.state = {
            nifty: "15320",
            bankNifty: "34452",
            cssNifty: "green",
            cssBankNifty: "red"
        }
        this.handleState = this.handleState.bind(this);
    }

   
    handleState = (key, data) => {
        this.setState({ [key]: data })
    }

    render() {


        const data = [
            {name: 'Geeksforgeeks', students: 400},
            {name: 'Technical scripter', students: 700},
            {name: 'Geek-i-knack', students: 200},
            {name: 'Geek-o-mania', students: 1000}
          ];

          
        const min = Math.round(Math.min(...this.props.data.map(o => o.premium)) - 5)
        const max = Math.round(Math.max(...this.props.data.map(o => o.premium)) + 5)

        return (
            <div>
                <br></br>
                <div style={{ "display": "flex", "width": "50%" }}>
                    <select class="form-select" aria-label="Default select example">
                        <option selected>Select Scrip</option>
                        <option value="Nifty">Nifty</option>
                        <option value="Bank Nifty">BankNifty</option>
                        <option value="Finnity">Finnity</option>
                    </select>
                    <select class="form-select" aria-label="Default select example">
                        <option selected>Select Expiry</option>
                        <option value="28 Jul 2022">28 Jul 2022</option>
                        <option value="4 Jul 2022">4 Jul 2022</option>
                    </select>
                </div>
                
                <div style={{ align: "center" }}>

                        <h3 className="text-center" style={{ marginTop: "5px"}}>
                            Nifty ATM - <h1 style ={{display : "inline"}} className={this.state.cssNifty}>{this.state.nifty}</h1>
                        </h3>

                   

                        <div className='middlealign'>
                            <ResponsiveContainer height='70%' width='100%'>
                                <LineChart data={this.props.data}>
                                    <CartesianGrid />
                                    <XAxis dataKey="time" angle={-45}
                                        interval={15} />
                                    <YAxis interval={2} tickCount={20} type="number" domain={[min, max]}></YAxis>
                                    <Legend />
                                    <Tooltip />
                                    <Line dataKey="premium" type='monotone' interval={10}
                                        stroke="blue" strokeWidth={2} activeDot={{ r: 8 }} dot={false} />
                                </LineChart>
                            </ResponsiveContainer>
                        </div>
                    </div>
            </div>
        );
    }
}

export default OIChart;
